import React, { Component } from "react";
import Slider from "react-slick";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { withTranslate } from "react-redux-multilingual";
import NumberFormat from "react-number-format";

import { getBestSeller } from "../../services";

class NewProduct extends Component {
  constructor() {
    super();
    this.state = {
      open: false,
      items: null,
    };
  }
  componentDidUpdate() {
    if (this.props.items.length > 0) {
      if (this.props.items !== this.state.items) {
        this.setState(() => {
          return {
            open: true,
            items: this.props.items,
          };
        });
      }
    }
  }

  componentDidMount() {
    if (this.props.items.length > 0) {
      if (this.props.items !== this.state.items) {
        console.log(this.props.items);
        this.setState(() => {
          return {
            open: true,
            items: this.props.items,
          };
        });
      }
    }
  }

  render() {
    var arrays = [];
    const { translate } = this.props;
    var settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 3,
    };

    let RatingStars = [];
    //console.log(product.average_rating);
    for (var i = 1; i <= parseInt(product.average_rating); i++) {
      RatingStars.push(<i className="fa fa-star" key={i + 20} />);
    }
    //console.log(RatingStars.length);
    if (RatingStars.length < 4) {
      var leftOff = 4 - RatingStars.length;
      //console.log(leftOff);
      for (i = 0; i <= leftOff; i++) {
        RatingStars.push(<i className="fa fa-star off" key={i} />);
      }
    }
    if (RatingStars.length > 5) {
      RatingStars.pop();
      // console.log(RatingStars);
    }

    const { symbol } = this.props;
    var items = this.state.items;
    console.log(items);
    if (this.state.items) {
      while (items.length > 0) {
        arrays.push(items.splice(0, 3));
        console.log(arrays);

        return (
          <div className="theme-card">
            <h5 className="title-border"> {translate("new_product")}</h5>
            <Slider className="offer-slider slide-1" {...settings}>
              {arrays.map((products, index) => {
                return (
                  <div data-index={index} key={index}>
                    {products.map((product, i) => {
                      return (
                        <div data-index={index} className="media" key={i}>
                          <Link
                            to={`${
                              process.env.PUBLIC_URL
                            }/left-sidebar/product/${product.id}`}
                          >
                            <img
                              className="img-fluid new-product-img"
                              src={`${product.pictures[0]}`}
                              alt=""
                            />
                          </Link>
                          <div className="media-body align-self-center">
                            <div className="rating d-f">
                              {RatingStars}
                              <h6 className="rating-count">
                                {" "}
                                (
                                {product.rating_count > 0
                                  ? product.rating_count + 1
                                  : product.rating_count}
                                )
                              </h6>
                            </div>
                            <Link
                              to={`${
                                process.env.PUBLIC_URL
                              }/left-sidebar/product/${product.id}`}
                            >
                              <h6>{product.name}</h6>
                            </Link>
                            <h4>
                              <NumberFormat
                                value={product.price}
                                displayType={"text"}
                                thousandSeparator={true}
                                prefix={"€"}
                                renderText={(formattedValue) => formattedValue} // <--- Don't forget this!
                              />
                            </h4>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                );
              })}
            </Slider>
          </div>
        );
      }
    } else {
      return <h6>{translate("loading")}</h6>;
    }
  }
}

function mapStateToProps(state) {
  return {
    items: state.data.products,
    symbol: state.data.symbol,
  };
}

export default connect(mapStateToProps)(withTranslate(NewProduct));
